const e={"nav.home":{en:"Home",zh:"首页"},"nav.about":{en:"About",zh:"关于"},"nav.projects":{en:"Projects",zh:"项目"},"nav.insights":{en:"Insights",zh:"设计洞察"},"nav.contact":{en:"Contact",zh:"联系"},"hero.greeting":{en:"hi, my name is",zh:"嗨，我是"},"hero.name":{en:"Wannz",zh:"Wannz"},"hero.title":{en:"Product Manager",zh:"产品经理"},"hero.emoji":{en:"👋",zh:"👋"},"hero.description":{en:"I'm a product manager with {0} and {1}, dedicated to bridging the gap between {2} and {3}. I excel at discovering user pain points through {4} and transforming them into {5}, making complex technology simple and easy to use.",zh:"我是一名具有{0}与{1}的产品经理，致力于在{2}与{3}之间架起桥梁。我擅长通过{4}发现用户痛点，并将其转化为{5}，让复杂的技术变得简单易用。"},"hero.highlight.passionate":{en:"passion",zh:"热情"},"hero.highlight.empathy":{en:"empathy",zh:"同理心"},"hero.highlight.userNeeds":{en:"user needs",zh:"用户需求"},"hero.highlight.technicalImpl":{en:"technical implementation",zh:"技术实现"},"hero.highlight.deepInsights":{en:"deep insights",zh:"深度洞察"},"hero.highlight.innovativeSolutions":{en:"innovative solutions",zh:"创新解决方案"},"hero.trait.empathy":{en:"User Empathy",zh:"用户同理心"},"hero.trait.innovation":{en:"Innovative Thinking",zh:"创新思维"},"hero.trait.technical":{en:"Technical Insight",zh:"技术洞察"},"hero.trait.focus":{en:"Goal Oriented",zh:"目标导向"},"hero.trait.execution":{en:"Efficient Execution",zh:"高效执行"},"hero.cta.work":{en:"View my work",zh:"查看我的作品"},"hero.cta.contact":{en:"Get in touch",zh:"联系我"},"hero.scroll":{en:"Scroll down to explore",zh:"向下滚动探索更多"},"home.productValues":{en:"Product Design Values",zh:"产品设计价值观"},"home.productValuesDesc":{en:"These core values guide my approach to product design and development, ensuring I create solutions that are both user-friendly and technically sound.",zh:"这些核心价值观指导着我的产品设计和开发方法，确保我创造的解决方案既用户友好又技术可靠。"},"home.stayTrue":{en:"Stay True",zh:"坚守初心"},"home.stayTrueDesc":{en:"Always remember the original purpose of the product, dare to admit problems and make decisive improvements.",zh:"始终铭记产品初心，敢于承认问题并果断改进。"},"home.rootCause":{en:"Root Cause",zh:"追根溯源"},"home.rootCauseDesc":{en:"Carefully explore the essence of problems, avoid doing just superficial work.",zh:"仔细探索问题本质，避免只做一些表面功夫。"},"home.simplicity":{en:"Simplicity",zh:"简约实用"},"home.simplicityDesc":{en:"Simplicity above all, rejecting unnecessary embellishment. Intuitive design is good design.",zh:"简单至上拒绝镀金，符合直觉的设计才是好的设计。"},"home.humanCentric":{en:"Human-Centric",zh:"以人为本"},"home.humanCentricDesc":{en:"Focus on user experience, with long-term value as the core orientation.",zh:"关注用户使用体验，以长期价值为核心导向。"},"home.featuredProjects":{en:"Recent Projects",zh:"近期项目"},"home.projectsDesc":{en:"Experience regarding work, achievements, and projects.",zh:"关于工作、经验和项目的成果、思考与复盘。"},"home.viewAllProjects":{en:"View all projects",zh:"查看全部项目"},"home.recentPosts":{en:"Recent Insights",zh:"近期洞察"},"home.postsDesc":{en:"Thoughts, ideas, and insights on design and the digital world.",zh:"关于设计、开发和数字世界的思考、想法和见解。"},"home.viewAllPosts":{en:"View all posts",zh:"查看全部洞察"},"common.knowMore":{en:"Want to know more about me?",zh:"想了解更多关于我的信息？"},"common.knowMoreDesc":{en:"If you have any questions about my work or projects, or would like to discuss further, feel free to contact me anytime.",zh:"如果你对我的工作或项目有任何疑问，或者想要进一步交流，欢迎随时联系我。"},"common.contactMe":{en:"Contact me",zh:"联系我"},"about.title":{en:"About Me",zh:"我的说明书"},"about.intro":{en:"Hello! I'm Wannz, a passionate developer and designer with a focus on creating beautiful, functional, and accessible digital experiences. With over 5 years of experience in the industry, I've worked on a wide range of projects from small business websites to large-scale enterprise applications.",zh:"你好！我是Wannz，一名充满激情的开发者和设计师，专注于创造美观、实用且易用的数字体验。凭借超过5年的行业经验，我参与过从小型企业网站到大型企业应用的各种项目。"},"about.profile.alt":{en:"Wannz profile picture",zh:"Wannz的个人照片"},"about.bio.p1":{en:"Hey, I'm Wannz, a product manager who prefers to be called a “product designer.” I decode user needs, execute strategies, and ensure a great user experience. My job is to turn vague ideas into tangible, user-focused features.",zh:"嘿，我是Wannz，更愿被称为“产品设计师”的产品经理。我既解读需求，也执行策略；既协调团队，也守护用户体验。我的工作是将模糊的用户需求转化为清晰的产品策略，让创意落地为实际功能。"},"about.bio.p2":{en:"As an “ENFJ-T with a touch of OCD,” I’m driven by idealism. It might seem impractical, but I believe it’s essential for creating truly outstanding products. I’ve worked in both startups and large companies, learning to be agile and detail-oriented while balancing user needs and business goals.",zh:"作为一名“有强迫症的ENFJ-T”，我的驱动力大多来自“理想主义”。这或许显得不够接地气，但我相信理想主义是推动产品卓越的基石。我曾在初创公司和大公司工作，学会了敏捷和注重细节，也明白了产品背后是活生生的人。"},"about.bio.p3":{en:"I navigate different teams and functions, coordinating efforts and ensuring efficiency. I’m sensitive to user feedback and strive for perfection, even if it keeps me up at night. I’m committed to creating products that truly meet user expectations.",zh:"我习惯在不同团队和职能之间穿梭，像“润滑剂”一样协调各方利益。我既是团队的“定海神针”，也是项目的“推进器”，确保每个环节高效运转。我敏感且有强迫症，用户的一句抱怨、一个bug，都会让我夜不能寐。我愿意在这条路上不断探索，只为做出让用户满意的产品。"},"about.skills.title":{en:"Skills & Technologies",zh:"技能与技术"},"about.skills.frontend":{en:"Frontend",zh:"前端"},"about.skills.backend":{en:"Backend",zh:"后端"},"about.skills.design":{en:"Design",zh:"设计"},"about.skills.tools":{en:"Tools",zh:"工具"},"about.skill.userInsight.name":{en:"User Research & Empathy",zh:"用户研究与共情"},"about.skill.userInsight.description":{en:"Conducting comprehensive user research to deeply understand user needs, pain points, and behaviors. Creating personas and journey maps to build empathy and guide product decisions.",zh:"进行全面的用户研究，深入理解用户需求、痛点和行为模式。创建用户画像和旅程地图，建立共情并指导产品决策。"},"about.skill.strategy.name":{en:"Product Strategy & Roadmapping",zh:"产品战略与路线规划"},"about.skill.strategy.description":{en:"Developing clear product vision and strategy aligned with business goals. Creating and prioritizing product roadmaps that balance user needs with technical feasibility and business value.",zh:"制定与业务目标一致的清晰产品愿景和战略。创建并优先排序产品路线图，平衡用户需求、技术可行性和商业价值。"},"about.skill.collaboration.name":{en:"Cross-functional Leadership",zh:"跨职能团队领导力"},"about.skill.collaboration.description":{en:"Leading cross-functional teams through the product development process. Effectively communicating with stakeholders at all levels and coordinating between design, engineering, and business teams.",zh:"在产品开发过程中领导跨职能团队。与各级利益相关者有效沟通，协调设计、工程和业务团队之间的合作。"},"about.skill.dataAnalysis.name":{en:"Data-Driven Optimization",zh:"数据驱动的优化"},"about.skill.dataAnalysis.description":{en:"Leveraging analytics to measure product performance and user behavior. Setting up experiments, analyzing results, and making data-informed decisions to continuously improve products.",zh:"利用分析工具衡量产品性能和用户行为。设计实验、分析结果，并基于数据做出决策，持续改进产品。"},"about.experience.title":{en:"Work experience",zh:"工作经历"},"about.career.description":{en:"Over the years, my professional journey has been like an exploration, with each step clear and determined. From my initial role in product operations in the internet industry, to growing into a product manager, and then to becoming a product leader and director, I've always maintained curiosity about the unknown and pursued excellence. Throughout this process, I've ventured into various product domains, whether user-facing internet applications, complex backend systems, or challenging platform and commercialization projects. From mobile applications to mini-programs, from desktop software to hardware products, and even vehicle cockpits and AI products, each experience has deepened my understanding of product design and management. I've also accumulated rich team management experience, leading teams composed of product managers, designers, and developers. As a core product leader, I've always worked closely with sales, delivery, operations, and development teams to tackle user and client challenges, creating value for both the team and company. Despite experiencing different roles and companies, I've always carried hopes for the future, continuously exploring and trying, all to find the answer that truly belongs to me.",zh:"在过去的岁月里，我的职业旅程如同一场探索之旅，每一步都清晰而坚定。从最初踏入互联网领域的产品运营，到逐步成长为产品经理，再到肩负重任的产品负责人和产品总监，我始终保持着对未知的好奇与对卓越的追求。在这个过程中，我涉足了多种产品领域，无论是直面用户的互联网应用，还是复杂的中后台系统，亦或是充满挑战的平台化与商业化项目，我都收获了宝贵的经验。从移动端应用到小程序，从桌面端软件到硬件产品，甚至还包括车机座舱与 AI 产品，每一次尝试都让我对产品设计与管理有了更深刻的理解。我也积累了丰富的团队管理经验，带领过由产品经理、设计师、研发人员等多角色组成的团队。作为核心产品负责人，我始终与销售、交付、运营、研发等团队紧密协作，共同攻克用户和客户的问题，为团队和公司创造价值。尽管历经不同角色与公司，我始终怀揣着对未来的期望，不断探索与尝试，只为找到真正属于自己的答案。"},"about.experience.job1.title":{en:"Product Director",zh:"产品总监"},"about.experience.job1.company":{en:"Finogeeks Technology Co., Ltd.",zh:"凡泰极客科技有限责任公司"},"about.experience.job1.period":{en:"2021 - Present",zh:"2021 - 至今"},"about.experience.job2.title":{en:"Senior Product Manager",zh:"高级产品经理"},"about.experience.job2.company":{en:"WIDEAL Technology Co., Ltd.",zh:"万物理想科技有限公司"},"about.experience.job2.period":{en:"2020 - 2021",zh:"2020 - 2021"},"about.experience.job3.title":{en:"Product & Project Manager",zh:"产品经理兼项目经理"},"about.experience.job3.company":{en:"GhostCloud Technology Co., Ltd.",zh:"精灵云科技有限公司"},"about.experience.job3.period":{en:"2019 - 2020",zh:"2019 - 2020"},"about.experience.job4.title":{en:"Product & Project Manager",zh:"产品经理兼项目经理"},"about.experience.job4.company":{en:"Knownsec Information Technology Co., Ltd.",zh:"知道创宇信息技术有限公司"},"about.experience.job4.period":{en:"2017 - 2019",zh:"2017 - 2019"},"projects.title":{en:"Projects",zh:"项目经历"},"projects.filter.all":{en:"All Projects",zh:"所有项目"},"projects.filter.featured":{en:"Featured",zh:"精选"},"projects.filter.web":{en:"Web",zh:"网站"},"projects.filter.mobile":{en:"Mobile",zh:"移动端"},"projects.filter.design":{en:"Design",zh:"设计"},"projects.filter.develop":{en:"Develop",zh:"开发"},"projects.filter.ai":{en:"AI",zh:"AI"},"projects.filter.category":{en:"Category",zh:"分类"},"projects.viewProject":{en:"View project",zh:"查看项目"},"projects.sourceCode":{en:"Source code",zh:"源代码"},"projects.description":{en:"A record of the product projects I've been involved in, which are a testament to my growth. Each project documents how I transformed requirements into actual features, and the process of solving problems within a team. The experiences may be ordinary, but they are my authentic accumulation.",zh:"记录我参与过的产品项目，它们是我成长的见证。每个项目都记录了我如何把需求变成实际功能，以及在团队里解决问题的过程。经历可能普通，但都是我真实的积累。"},"projects.viewAll":{en:"View all projects",zh:"查看所有项目"},"projects.other":{en:"Other Projects",zh:"其他项目"},"projects.cta":{en:"Explore All Projects",zh:"探索所有项目"},"projects.github":{en:"GitHub",zh:"GitHub"},"projects.live":{en:"Live Demo",zh:"在线演示"},"projects.year":{en:"Year",zh:"年份"},"projects.client":{en:"Client",zh:"客户"},"projects.role":{en:"Role",zh:"角色"},"projects.backToProjects":{en:"Back to Projects",zh:"返回项目列表"},"projects.noProjects.title":{en:"No projects found",zh:"未找到项目"},"projects.noProjects.description":{en:"There are currently no projects in this category. Please check back later or explore other categories.",zh:"当前类别中没有项目。请稍后再查看或探索其他类别。"},"project.details.overview":{en:"Project Overview",zh:"项目概述"},"project.details.technologies":{en:"Technologies Used",zh:"使用的技术"},"project.details.role":{en:"My Role",zh:"我的角色"},"project.details.duration":{en:"Duration",zh:"持续时间"},"project.details.year":{en:"Year",zh:"年份"},"project.details.client":{en:"Company",zh:"公司"},"project.details.status":{en:"Status",zh:"状态"},"project.details.liveDemo":{en:"Live Demo",zh:"在线演示"},"project.details.sourceCode":{en:"Source Code",zh:"源代码"},"project.details.backToProjects":{en:"Back to Projects",zh:"返回项目列表"},"project.details.nextProject":{en:"Next Project",zh:"下一个项目"},"project.details.previousProject":{en:"Previous Project",zh:"上一个项目"},"project.status.completed":{en:"Completed",zh:"已完成"},"project.status.inProgress":{en:"In Progress",zh:"进行中"},"project.status.planning":{en:"Planning",zh:"规划中"},"project.status.discontinued":{en:"Discontinued",zh:"已下线"},"projects.viewOnGithub":{en:"View on GitHub",zh:"在GitHub上查看"},"projects.viewLiveDemo":{en:"View Live Demo",zh:"查看在线演示"},"insights.title":{en:"Design Insights",zh:"产品洞察"},"insights.description":{en:"A record of my thoughts on product design. Such as how to understand user needs, plan product direction, and use data to optimize products. The content may be basic, but these are things I find important in my work. Read often, stay fresh.",zh:"记录一些我对产品设计的思考。比如怎样理解用户需求、规划产品方向，以及用数据来优化产品。内容可能基础，但都是我工作中觉得重要的东西。常读常新。"},"insights.visitBlog":{en:"Visit my main blog at hiwannz.com",zh:"访问我的主博客 hiwannz.com"},"insights.noArticles":{en:"No articles yet",zh:"暂无文章"},"insights.checkBack":{en:"Check back soon for new articles and insights.",zh:"请稍后再来查看新文章和见解。"},"blog.title":{en:"Latest Articles",zh:"最新文章"},"blog.viewAll":{en:"View all articles",zh:"查看所有文章"},"blog.readMore":{en:"Read more",zh:"阅读更多"},"blog.cta":{en:"Explore All Articles",zh:"浏览所有文章"},"blog.minRead":{en:"min read",zh:"分钟阅读"},"contact.title":{en:"Get in Touch",zh:"与我联系"},"contact.description":{en:"I'm always open to discussing product design, user experience, or potential collaborations. While I keep things simple, I value every connection and conversation.",zh:"我始终愿意讨论产品设计、用户体验或潜在合作。虽然我保持简单的联系方式，但我珍视每一次连接和交流。"},"contact.email.title":{en:"Email Me",zh:"发送邮件"},"contact.email.description":{en:"For project inquiries, questions, or just to say hello, feel free to send me an email. I typically respond within 24-48 hours.",zh:"如有项目咨询、问题或只是想打个招呼，请随时给我发送邮件。我通常会在24-48小时内回复。"},"contact.blog.title":{en:"Leave a Comment",zh:"留言评论"},"contact.blog.description":{en:"You can also reach me by leaving a comment on any of my blog posts. I regularly check and respond to comments on my blog.",zh:"你也可以在我的任何博客文章下留言与我联系。我会定期查看并回复博客上的评论。"},"contact.info.follow":{en:"Follow Me",zh:"关注我"},"changelog.title":{en:"Changelog",zh:"更新日志"},"changelog.description":{en:"A log of updates, improvements, and new features added to this website.",zh:"本网站添加的更新、改进和新功能的日志。"},"changelog.version":{en:"Version",zh:"版本"},"changelog.date":{en:"Date",zh:"日期"},"changelog.v1.title":{en:"Complete Rebuild with International Support",zh:"全新重构，迈向国际化"},"changelog.v1.description":{en:"With Claude's assistance, I completed a complete website rebuild. This time, I chose the Astro framework, which not only provides excellent performance but also allows me to create a more personalized and attractive website layout from a content perspective. The biggest highlight of this update is that the website fully supports bilingual content in both Chinese and English, allowing visitors to enjoy a seamless language switching experience and access more comprehensive, rich content. This not only enhances the user experience but also gives me the opportunity to communicate and share with a wider audience.",zh:"在Claude的协助下，我完成了网站的全新重构。这一次，我选择了Astro框架，它不仅提供了出色的性能，还让我能够从内容的角度出发，打造出更加个性化、更具吸引力的网站布局。这次改版最大的亮点是网站全面支持中英文双语内容，无论是中文访客还是英文访客，都能享受到无缝切换的语言体验，获取更加全面、丰富的内容。这不仅提升了用户体验，也让我有机会与更广泛的受众进行交流和分享。"},"changelog.v03.title":{en:"Bilingual Support with Refreshed Interface",zh:"双语国际化，界面焕然一新"},"changelog.v03.description":{en:"In this version, I introduced i18n features to the existing website, making all pages support switching between Chinese and English. This improvement greatly enhanced the internationalization of the website, allowing visitors from different language backgrounds to browse with ease. At the same time, I optimized the website's style, adopting a more concise and clean design style, making the entire website more comfortable and user-friendly. This update not only improved the functionality of the website but also enhanced its visual appeal.",zh:"在这一版本中，我在原有网站的基础上引入了i18n特性，使所有页面都支持中英文切换。这一改进极大地提升了网站的国际化程度，让不同语言背景的访客都能轻松浏览。同时，我对网站的样式进行了优化调整，采用了更加简洁、清爽的设计风格，让整个网站看起来更加舒适、易用。这次改版不仅提升了网站的功能性，也增强了视觉上的吸引力。"},"changelog.v02.title":{en:"Enriched Project Content and Interaction",zh:"项目内容丰富，互动开启"},"changelog.v02.description":{en:"Initially, the website only had a homepage with limited content. To better showcase my project experience and achievements, I added independent project pages with detailed introductions to the various projects I've been involved in. This improvement made the website content richer and gave me the opportunity to have deeper exchanges with visitors. Surprisingly, after this update, I received emails from internet friends, which made me deeply feel the wonderful connections of the online world and motivated me to continue improving the website content.",zh:"最初，网站只有首页，内容略显单薄。为了更好地展示我的项目经验和成果，我新增了独立的项目页面，详细介绍了我参与的各个项目。这一改进让网站内容更加丰富，也让我有机会与访客进行更深入的交流。令人惊喜的是，这次改版后，我收到了网友的邮件，这让我深刻感受到网络世界的奇妙连接，也激励我继续完善网站内容。"},"changelog.v01.title":{en:"Bootstrap Rebuild Taking Shape",zh:"Bootstrap重构，初具雏形"},"changelog.v01.description":{en:"After completing the project documentation and homepage planning, I decided to use the Bootstrap framework for the initial website design. With the powerful features of Bootstrap and my own designed CSS styles, I successfully built a clean, practical website and launched it online. Although the content was not yet comprehensive, this update laid the foundation for subsequent optimizations and gave me more motivation to add more detailed content and experiences.",zh:"在完成项目文档和首页规划后，我决定使用Bootstrap框架进行网站的初步设计。借助Bootstrap的强大功能和自己设计的CSS样式，我成功搭建了一个简洁、实用的网站，并将其上线。虽然内容还不够完善，但这次改版为后续的优化奠定了基础，也让我有了更多动力去补充更详细的内容和经历。"},"changelog.v0.title":{en:"Dream Sets Sail",zh:"梦想起航"},"changelog.v0.description":{en:"As a child, I enjoyed writing my own websites using HTML and CSS, but most were small endeavors that never became complete online websites. In 2018, I decided to take this step and create a truly usable, reliable online website according to my own ideas. The birth of this idea started my journey in website design and kept me moving forward in technical exploration and content creation.",zh:"小时候，我就喜欢用HTML和CSS编写自己的网站，但大多是小打小闹，从未真正实现一个完整的线上网站。2018年，我决定迈出这一步，按照自己的想法，打造一个真正可用、可靠的线上网站。这个想法的诞生，开启了我网站设计的旅程，也让我在技术探索和内容创作上不断前行。"},"legal.title":{en:"Legal Notice",zh:"法律说明"},"legal.description":{en:"Important legal information about this website, including copyright, liability, and privacy policies.",zh:"关于本网站的重要法律信息，包括版权、责任和隐私政策。"},"legal.copyright.title":{en:"Copyright Notice",zh:"版权声明"},"legal.copyright.content":{en:"All content on this website (including but not limited to text, images, audio, and video materials) is owned by Wang Zi and protected by Chinese laws and international conventions. Without permission, no reproduction, republication, or distribution of the content on this website is allowed in any form.",zh:"本网站内容（包括但不限于文字、图片、音频、视频资料）的版权均归 Wang Zi 所有，受中国法律和国际公约保护。未经许可，不得以任何形式复制、转载、传播本网站内容。"},"legal.copyright.additional":{en:"All original works published on this site are licensed under a Creative Commons Attribution-NonCommercial 4.0 International License unless otherwise stated. This means you are free to share and adapt the materials for non-commercial purposes, provided you give appropriate credit to the original author.",zh:"除非另有说明，本网站发布的所有原创作品均采用知识共享署名-非商业性使用 4.0 国际许可协议进行许可。这意味着您可以自由分享和改编这些材料用于非商业目的，前提是您必须给予原作者适当的署名。"},"legal.liability.title":{en:"Liability for Content",zh:"内容责任声明"},"legal.liability.content":{en:'As the operator of this website, I solemnly declare that this website strictly follows the requirements of the "Provisions on the Governance of the Online Information Content Ecosystem" and relevant laws and regulations. I have comprehensively reviewed all content on the website to ensure its legality and compliance, and I am responsible for the authenticity and legality of the website content.',zh:"作为本网站的运营者，我郑重声明，本网站严格遵循《网络信息内容生态治理规定》及相关法律法规的要求。我已对网站中的所有内容进行了全面审核，确保其合法合规，并对网站内容的真实性、合法性负责。"},"legal.liability.additional":{en:'If you, as a rights holder, discover any content on the website that may infringe upon your legitimate rights, including but not limited to copyright, portrait rights, reputation rights, etc., please contact me immediately. Upon receiving your notification, in accordance with Articles 1195 and 1197 of the "Civil Code", I will promptly review the relevant content and, after confirming the infringement, quickly take necessary measures such as deletion, blocking, or disconnecting links. At the same time, I will actively cooperate with your reasonable requests to ensure that your legitimate rights and interests are fully protected.',zh:"如果您作为权利人发现网站中存在任何可能侵犯您合法权益的内容，包括但不限于著作权、肖像权、名誉权等，请您在第一时间与我联系。我将在收到您的通知后，依据《民法典》第1195条和第1197条的规定，对相关内容进行及时审查，并在确认侵权事实后迅速采取删除、屏蔽或断开链接等必要措施。同时，我将积极配合您的合理请求，确保您的合法权益得到充分保护。"},"legal.privacy.title":{en:"Privacy Policy",zh:"隐私政策"},"legal.privacy.content":{en:"This website values user privacy protection. User information collected is only used to improve website service quality and user experience. Without user consent, no information that may identify the user will be disclosed to third parties, except as otherwise provided by laws and regulations.",zh:"本网站重视用户隐私保护，收集的用户信息仅用于改善网站服务质量和用户体验。未经用户同意，不会向第三方披露任何可能识别用户身份的信息，法律法规另有规定的除外。"},"legal.privacy.additional":{en:"This website uses cookies to enhance user experience. These cookies are small text files stored on your device that help analyze how users interact with the site. You can configure your browser to refuse cookies, but this may affect your browsing experience. By using this website, you consent to the use of cookies in accordance with this privacy policy.",zh:"本网站使用 cookies 来增强用户体验。这些 cookies 是存储在您设备上的小型文本文件，有助于分析用户如何与网站互动。您可以配置浏览器拒绝 cookies，但这可能会影响您的浏览体验。使用本网站即表示您同意根据本隐私政策使用 cookies。"},"legal.privacy.dataCollection":{en:"Information collected may include: IP address, browser type, operating system, pages visited, time spent on the site, and referring websites. This data is used for analytical purposes to improve the website and is not shared with third parties for commercial purposes.",zh:"收集的信息可能包括：IP 地址、浏览器类型、操作系统、访问的页面、在网站上花费的时间以及引荐网站。这些数据用于分析目的以改进网站，不会出于商业目的与第三方共享。"},"legal.externalLinks.title":{en:"External Links",zh:"外部链接"},"legal.externalLinks.content":{en:"This website may contain links to third-party websites, which are provided for the convenience of users. This website is not responsible for the content of these external websites, nor does it represent the endorsement of their views or content by this website.",zh:"本网站可能包含指向第三方网站的链接，这些链接仅为方便用户而提供。本网站对这些外部网站的内容不承担任何责任，也不代表本网站认可其观点或内容。"},"legal.changes.title":{en:"Changes to Legal Notice",zh:"法律声明变更"},"legal.changes.content":{en:"This legal notice may be updated from time to time. Any changes will be posted on this page, and the date of the last update will be indicated at the bottom of the page. Users are encouraged to check this page periodically for any changes.",zh:"本法律声明可能会不时更新。任何变更将发布在此页面上，最后更新日期将在页面底部注明。建议用户定期查看此页面以了解任何变更。"},"sourceCode.title":{en:"Source Code",zh:"项目源代码"},"sourceCode.description":{en:"I have open-sourced my personal introduction website as a front-end project on GitHub. This project not only documents my growth and experiences but also aims to provide some insights and inspiration for other developers. Open-sourcing is about sharing and progressing together. I look forward to connecting with more like-minded individuals.",zh:"我将我的个人介绍网站开源为一个前端项目，分享在GitHub上。这个项目不仅记录了我的成长和经验，也希望能为其他开发者提供一些参考和灵感。开源是为了分享，也是为了共同进步，期待与更多志同道合的朋友交流。"},"sourceCode.about.title":{en:"About This Project",zh:"关于本项目"},"sourceCode.about.content":{en:"This personal website project is built with the Astro framework, using modern frontend technologies including TailwindCSS, Lucide icons, and shadcn/ui components. The website design follows minimalist principles, focusing on content presentation and user experience, while supporting Chinese and English internationalization.",zh:"这个个人网站项目是基于 Astro 框架构建的，采用了现代化的前端技术栈，包括 TailwindCSS、Lucide 图标和 shadcn/ui 组件库。网站设计遵循极简主义原则，注重内容展示和用户体验，同时支持中英文国际化。"},"sourceCode.techStack.title":{en:"Technology Stack",zh:"技术栈"},"sourceCode.techStack.framework":{en:"Framework:",zh:"框架："},"sourceCode.techStack.styling":{en:"Styling:",zh:"样式："},"sourceCode.techStack.icons":{en:"Icons:",zh:"图标："},"sourceCode.techStack.components":{en:"UI Components:",zh:"UI组件："},"sourceCode.techStack.content":{en:"Content Management:",zh:"内容管理："},"sourceCode.techStack.i18n":{en:"Internationalization:",zh:"国际化："},"sourceCode.repository.title":{en:"GitHub Repository",zh:"GitHub 仓库"},"sourceCode.repository.description":{en:"The source code for this project is open source on GitHub. Feel free to check it out, use it, or contribute.",zh:"本项目的源代码已在 GitHub 上开源，欢迎查看、使用或贡献代码。"},"sourceCode.repository.button":{en:"View Source Code",zh:"查看源代码"},"sourceCode.license.title":{en:"License",zh:"使用许可"},"sourceCode.license.content":{en:"This project is licensed under the Creative Commons Attribution-NonCommercial-ShareAlike 4.0 International License (CC BY-NC-SA 4.0). This means you are free to share and adapt the materials for non-commercial purposes, as long as you give appropriate credit, provide a link to the license, and indicate if changes were made. If you remix, transform, or build upon the material, you must distribute your contributions under the same license as the original. For more information, visit the Creative Commons website.",zh:"本项目采用知识共享署名-非商业性使用-相同方式共享 4.0 国际许可协议（CC BY-NC-SA 4.0）授权。这意味着您可以自由地分享和改编本项目的材料用于非商业目的，前提是您必须给出适当的署名，提供许可协议的链接，并指明是否做了修改。如果您对材料进行了混合、转换或者基于本项目进行创作，您必须以相同的许可协议分发您的贡献。更多信息，请访问知识共享组织网站。"},"legal.lastUpdated":{en:"Last Updated",zh:"最后更新"},"404.title":{en:"Page Not Found",zh:"页面未找到"},"404.description":{en:"The page you are looking for doesn't exist or has been moved.",zh:"您要查找的页面不存在或已被移动。"},"404.goHome":{en:"Go back home",zh:"返回首页"},"404.explore":{en:"Explore projects",zh:"浏览项目"}};export{e as translations};
